mapper.update:
update room_detail
set delete_flag = 1
where id = #{id}

mapper.select:
select rd.id,rd.room_id roomId,s.student_name studentName,c.college_name collegeName,cs.class_name className,s.student_sex studentSex,s.student_phone studentPhone,rd.check_date checkDate,rd.student_id studentId, s.student_code studentCode from room_detail rd inner join student s on rd.student_id = s.id inner join college c on s.college_id = c.id inner join class cs on s.class_id = cs.id
rd.delete_flag = 0 and rd.room_id in
#{item}

mapper.update:
update room_detail set delete_flag = 1
id in
#{item}

mapper.select:
select id,student_id studentId
from room_detail
where room_id = #{id}

select r.room_code roomCode,b.building_code buildingCode,s.student_name studentName,c.college_name collegeName,cs.class_name className,s.student_sex studentSex,s.student_phone studentPhone,rd.check_date checkDate,s.student_code studentCode from room_detail rd inner join room r on rd.room_id = r.id inner join building b on r.building_id = b.id inner join student s on rd.student_id = s.id inner join college c on s.college_id = c.id inner join class cs on s.class_id = cs.id
rd.delete_flag = 0
and b.id = #{buildingId}
and r.id = #{roomId}
and c.college_id = #{collegeId}
and cs.id = #{classId}
and s.student_code like concat('%',#{studentCode},'%')
and s.student_name like concat('%',#{studentName},'%')
and rd.check_date = #{checkDate}
